'use strict';
var $ = jQuery;

var Router = require('app/router/pushstate/router');

var PagesController = require('app/pages/controller');
var TerminalController = require('app/terminal/controller');

var Commands = require('app/commands/commands');
var NavCommand = require('app/commands/pages/navigation');

$(function(){

  var pagesController = new PagesController($('.main-screen-turn-on'));
  new TerminalController($('.terminal'));

  var router = new Router({
    pages: pagesController,
    root: {id: 'articles/about', controller: 'pages'}
  });

  Commands.register();
  new NavCommand(router);

  console.log("Hi!");
});
