'use strict';

var $ = require('jquery');
var Router = require('app/router/router');
var ArticlesController = require('app/articles/controller');
var LinkHijacker = require('app/link_hijacker/controller');
var Terminal = require('app/terminal/controller');

var Adventure = require('app/adventure/commander');
var Snark = require('app/snark/commander');
var TerminalCommander = require('app/terminal/commander');
var Articles = require('app/articles/commander');
var Menu = require('app/menu/commander');


$(function(){

  var articlesController = new ArticlesController($('.main-window'));
  var router = new Router({
    articles: articlesController,
    root: {id: 'welcome', controller: 'articles'}
  });

  new LinkHijacker();
  new Articles(router);
  new Terminal( $('.terminal') );

  new Menu();
});
