'use strict';

var $ = require('jquery');
var Router = require('app/router/hashchange/router');
var PagesController = require('app/pages/controller');
var LinkHijacker = require('app/link_hijacker/controller');
var Terminal = require('app/terminal/controller');

var Adventure = require('app/adventure/commander');
var Snark = require('app/snark/commander');
var TerminalCommander = require('app/terminal/commander');
var Pages = require('app/pages/commander');
var Menu = require('app/menu/commander');


$(function(){

  var pagesController = new PagesController($('.main-window'));
  var router = new Router({
    pages: pagesController,
    root: {id: 'welcome', controller: 'pages'}
  });

  new LinkHijacker();
  new Pages(router);
  new Terminal( $('.terminal') );

  new Menu();
});
