var Store = require('app/articles/store');
var View = require('app/articles/view');

function Controller($container){
  var article, view;

  this.load = function(articleName){
    Store.find(articleName).then(function(article){
      View.create($container, article);
    });
  };

}

module.exports = {
  create: function($container){
    return new Controller($container);
  }
};
