var Model = require('app/articles/model');
var $ = require('jquery');

var load = function(articleName, deferred){
  $.ajax({
    method: 'GET',
    url:'articles/filler.html'
  })
  .then(function(data){

    var model = Model.create({
      id: 'lebowski',
      body: data,
    });

    deferred.resolve(model);

  });
};

module.exports = {
  find: function(articleName){

    //use jquery promise as polyfill for IE :(
    var deferred = $.Deferred();

    load(articleName, deferred);

    return deferred.promise();
  },
};
