var Model = require('app/articles/model');
var Promise = require('app/utils/promise');
var $ = require('jquery');

var load = function(articleName){
  return $.ajax({
    method: 'GET',
    url:'articles/filler.html'
  });
};

module.exports = {
  find: function(articleName){
    var modelPromise = Promise.create();

    load(articleName).then(function(data){
      var model = Model.create({
        id: 'lebowski',
        body: data,
      });

      modelPromise.resolve(model);
    });

    return modelPromise;
  },
};
