"use strict";

var terminal = require('app/terminal/model');

function Commander(){
  var showHelp = function(){
    var lines = [
      ["Some commands to try:"],
      ["nav", {data: {input: "nav"}}],
      ["page", {data: {input: "page"}}],
      ["github", {data: {input: "github"}}],
      ["linkedin", {data: {input: "linkedin"}}],
      ["quit/exit/q", {data: {input: "quit"}}],
    ];

    lines.forEach(function(line){
      terminal.log.apply(terminal, line);
    })
  };

  terminal.registerCommand({
    matcher: "help",
    fn: showHelp,
  });

}

module.exports = Commander;

