"use strict";

var MenuModel = require('app/menu/model');
var MenuView = require('app/menu/view');

var terminal = require('app/terminal/model');

function Commander(){
  var displayMenu, menu, view;

  menu = new MenuModel();

  view = new MenuView(menu);
  displayMenu = function(match) {

    view.build(menu.nodes).forEach(function(line){
      terminal.log(line);
    });
  };

  terminal.registerCommand({
    matcher: /^menu$/,
    fn: displayMenu,
  });

}

module.exports = Commander;

