"use strict";

var terminal = require('app/terminal/model');

function View(menu){
  this.menu = menu;
}

View.prototype.build = function(rootNode){
  return walk(rootNode, '* ');
};


var walk = function(parentNode, prefix){
  var lines = [];
  parentNode.forEach(function(node){
    var $el = $('<a></a>');
    $el.attr('data-input', node.input);
    $el.text(prefix + node.display);
    lines.push($el); 

    if (node.subNodes.length > 0){
      lines = lines.concat(walk(node.subNodes, '*'+prefix));
    }
  });

  return lines;
};
module.exports = View;
