"use strict";

var MobileDetector = require('app/utils/mobile/detector');
var Model = require('app/mobile/model');

function Controller(){
  var detectedMobile = MobileDetector.detect();
  this.model = new Model(detectedMobile);

  this.getState = this.model.getState.bind(this.model);
  this.isMobile = this.model.isMobile.bind(this.model);
}

module.exports = Controller;
