"use strict";

var textMap = {
  'confirmed': "You said you're not on mobile",
  'likely': "You may be on mobile",
  'unlikely': "You are probably not on mobile",
  'denied': "You said you're not on mobile",
};

function View($el, model){
  this.$el = $el;
  this.update(model.state, model.isMobile());
}

View.prototype.update = function(state, isMobile){
  var button = '';

  if (isMobile){
    button = '<a data-input="terminal enable">Enable terminal</a>';
  }
  
  this.$el.html([
    '<p class="notification">' + textMap[state] + '</p>',
    button
  ]);

};

module.exports = View;
