"use strict";

var $ = require('jquery');

var textMap = {
  'confirmed': "You said you're not on mobile",
  'likely': "You may be on mobile",
  'unlikely': "You are probably not on mobile",
  'denied': "You said you're not on mobile",
};

function View($el, model){
  this.$el = $el;
  this.$enableButton = $('<a data-input="terminal enable">Enable terminal</a>');
  this.$disableButton = $('<a data-input="terminal disable">Disable terminal</a>');

  this.update(model.state);
}

View.prototype.update = function(state, isMobile){
  var $button = isMobile ? this.$disableButton : this.$enableButton;
  
  this.$el.html([
    '<p class="notification">' + textMap[state] + '</p>',
    $button
  ]);

};

module.exports = View;
