var downloaders = require('app/articles/cache_machine/registry');

//TODO: Should only enqueue a certain number at a time

var start = function(){
  Object.keys(downloaders).forEach(function(key) {
    downloaders[key].getArticle();
  });
};

var get = function(name){
  return downloaders[name].getArticle();
};

module.exports = {
  get: get,
  start: start,
};
