var Model = require('app/pages/model');
var $ = require('jquery');

function HiddenDivLoader(page){
  this.name = page.name;
  this.$page = $('[data-page-name="'+name+'"]');
  this.deferred = $.Deferred();
  this.promise = this.deferred.promise();
  this.state = 'pending';
}

HiddenDivLoader.prototype.canLoad = function(){
  return (this.$page.length > 0);
};

HiddenDivLoader.prototype.getPage = function(){
  if (this.state === 'pending'){
    var model = Model.create({
      body: this.$page.html(),
    });
    this.deferred.resolve(model);
  }

  return this.promise;
};

module.exports = HiddenDivLoader;
