var Store = require('app/pages/store');
var View = require('app/pages/view');
var terminal = require('app/terminal/model');

function Controller($container){
  this.load = function(pageName){
    try {
      Store.find(pageName).then(function(page){
        View.create($container, page);
      });
    }
    catch(e) {
      message = 'Page "' + pageName + '" does not exist';
      terminal.warn(message);

      throw Error(message);
    }
  };

}

module.exports = Controller;
