var commandRegistry = require('app/terminal/commands/registry');
var terminal = require('app/terminal/model');

function Commander(router){
  var command, run;
  this.snark = 0;

  run = function(match){
    terminal.warn(snarks[this.snark%snarks.length]);
    this.snark += 1;
  };

  command = commandRegistry.register({
    matcher: /^\s*$/,
    fn: run.bind(this),
  });
}

var snarks = [
  "You didn't type anything...",
  "Why would you type nothing",
  "I'm afraid I can't do that, Dave",
];

module.exports = new Commander();
