var terminal = require('app/terminal/model');

function Commander(){

  run = function(match){
    terminal.warn("so long!");
    terminal.close();
  };

  terminal.registerCommand({
    matcher: "exit",
    fn: run.bind(this),
  });
  
  terminal.registerCommand({
    matcher: "q",
    fn: run.bind(this),
  });

  terminal.registerCommand({
    matcher: "quit",
    fn: run.bind(this),
  });
}

module.exports = new Commander();
