var OutputView = require('app/terminal/output/view');
var InputView = require('app/terminal/input/view');
var Processor = require('app/terminal/processor');

function Controller($container){
  var $output, $input, processor, outputView, inputView;

  $output = $container.find('.terminal-output');
  $input = $container.find('.terminal-input');

  processor = new Processor();

  outputView = new OutputView($output, processor); 
  inputView = new InputView($input, processor);

  $input.focus();

}

module.exports = Controller;
