"use strict";

var terminal = require('app/terminal/model');
var TerminalToggler = require('app/terminal/commands/terminal_toggler');

function ClickHandler(mobileController){
  this.mobileController = mobileController;
  this.enabled = ! mobileController.isMobile();

  new TerminalToggler(this);
}

ClickHandler.prototype.handle = function(clickedTerminal){
  if (clickedTerminal && this.enabled){
    terminal.open();
  }
  else {
    terminal.close();
    console.log('start typing');
    terminal.type('page welcome').then(function(){console.log('done typing')});
  }
};

module.exports = ClickHandler;
