"use strict";

function View($el){
  this.$el = $el;
}

View.prototype.enabled = function(){
  this.$el.find('.js-terminal-enable').hide();
  this.$el.find('.js-terminal-disable').show();
};

View.prototype.disabled = function(){
  this.$el.find('.js-terminal-enable').show();
  this.$el.find('.js-terminal-disable').hide();
};

module.exports = View;
