var terminal = require('app/terminal/model');

function View($el, inputModel){
  this.$el = $el;
  this.input = inputModel;

  this.input.events.on('enabled', show.bind(this));
  this.input.events.on('disabled', hide.bind(this));
}

//private, must be bound

var hide = function(){
  this.$el.hide();
};

var show = function(){
  this.$el.attr('data-prompt-name', this.input.getPromptName());
  this.$el.show();
  this.$el.focus();
  this.$el.closest('.terminal').scrollTop(10000);
};

module.exports = View;
