var EventEmitter = require('events').EventEmitter;

function View($container, processor){
  this.$container = $container;

  var events = this.events =  new EventEmitter();

  this.$container.on('keypress', function(event){
    if (event.keyCode === 13) {
      processor.process(event.target.value);
    }
  });
}

module.exports = View;
