EventEmitter = require('events').EventEmitter;

function Model(processor){
  processor.events.on('processed', this.add.bind(this));
  this.events = new EventEmitter();
}

Model.prototype.add = function(input, response) {
   
  this.events.emit('lineAdded', {type: 'input', text: input});
  this.events.emit('lineAdded', {type: 'output', text: response});
};

module.exports = Model;
