var $ = require('jquery');
var model = require('app/terminal/output/model');

function View($container){
  this.$el = $container;
  model.events.on('lineAdded', this.addLine.bind(this));
}

View.prototype.addLine = function(line){
  var css;

  if (line.type === 'input') {
    $line = $('<p class="terminal-output--in"></p>');
  }
  else {
    $line = $('<p class="terminal-output--out"></p>');
  }

  $line.text(line.text);
  this.$el.append($line);
};

module.exports = View;
