$ = require('jquery');

function View($container, processor){
  this.$container = $container;
  this.render = this.render.bind(this);

  processor.events.on('processed', this.render);
}

View.prototype.render = function(input, output){
  var $input, $output;

  $input = $('<p class="terminal-output--in"></p>');
  $output = $('<p class="terminal-output--out"></p>');
  $input.text(input);
  $output.text(output);

  this.$container.append($input);
  this.$container.append($output);
};

module.exports = View;
