var CommandRegistry = require('app/terminal/command_registry/registry');

function Processor(name){
  this.name = name;
  this.commandRegistry = new CommandRegistry();

  this.process = process.bind(this);

  //delegate
  this.registerCommand = this.commandRegistry.register.bind(this.commandRegistry);
}

var process = function(input){
  var handler, deferred;

  handler = this.commandRegistry.findHandler(input);

  return handler.run(input);
};

module.exports = Processor;
