var commandRegistry = require('app/terminal/commands/registry');
var terminal = require('app/terminal/model');

function Commander(){

  run = function(match){
    terminal.warn("so long!");
    terminal.close();
  };

  commandRegistry.register({
    matcher: /^\s*exit\s*$/,
    fn: run.bind(this),
  });
}

module.exports = new Commander();
