var Prompt = require('app/terminal2/input/prompt/model');
var terminal = require('app/terminal2/model');

var View = require('app/terminal2/view');
var InputController = require('app/terminal2/input/controller');

var $ = require('jquery');

function Controller($el){
  new InputController($el, terminal.input);

  new View($('body'));

  $('body').on('click', function(event){
    if (inTerminalView(event.target)){
      terminal.open();
    }
    else {
      terminal.close();
    }
  });
}

var inTerminalView = function(el){
 return $.contains( $('.terminal')[0], el) ||  ( $('.terminal')[0] == el );
};

module.exports = Controller;
