var $ = require('jquery');
var model = require('app/terminal/model');

function View($container){
  this.$el = $container;
  model.events.on('lineAdded', this.addLine.bind(this));
}

View.prototype.addLine = function(line){
  var $line, css, promptName;
  
  css = 'terminal-output--' + line.type;

  $line = $('<p class="terminal-output"></p>');

  $line.addClass(css);

  if (line.data && line.data.promptName){
    $line.attr('data-prompt-name', line.data.promptName);
  }

  $line.text(line.text);
  this.$el.append($line);
  //$line[0].scrollIntoView();
};

module.exports = View;
