var charCodeMap = require('app/terminal/input/char_code_map');

var specialChars = [
  'backspace',
  'enter',
  'left_arrow',
  'right_arrow',
  'up_arrow',
  'down_arrow',
  'delete',
];

var getSpecial = function(charCode){
  var typedChar = charCodeMap.getChar(charCode);

  if ( specialChars.indexOf(typedChar) > -1 ) {
    return typedChar;
  } 
  else {
    return false;
  }
};

module.exports = {
  getSpecial: getSpecial,
};
